import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import BookingList from './components/Booking/BookingList';
import BookingDetails from './components/Booking/BookingDetails';
import UserList from './components/User/UserList';
import UserDetails from './components/User/UserDetails';
import Dashboard from './components/Dashboard';
import NotFound from './components/NotFound';
import { getUserRole } from './utils/auth'; // Assume a function to retrieve user role

function App() {
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    // Assume an asynchronous function to get the user role
    const fetchUserRole = async () => {
      try {
        const role = await getUserRole(); // Get the user role
        setUserRole(role);
      } catch (error) {
        console.error('Error fetching user role:', error);
      }
    };

    fetchUserRole();
  }, []);

  return (
    <Routes>
      <Route
        path="/register"
        element={
          userRole === 'admin' ? (
            <Register />
          ) : (
            <Navigate to="/dashboard" />
          )
        }
      />
      <Route
        path="/bookings"
        element={
          userRole === 'admin' ? (
            <BookingList />
          ) : (
            <Navigate to="/dashboard" />
          )
        }
      />
      <Route
        path="/bookings/:id"
        element={
          userRole === 'admin' ? (
            <BookingDetails />
          ) : (
            <Navigate to="/dashboard" />
          )
        }
      />
      <Route
        path="/users"
        element={
          userRole === 'admin' ? (
            <UserList />
          ) : (
            <Navigate to="/dashboard" />
          )
        }
      />
      <Route
        path="/users/:id"
        element={
          userRole === 'admin' ? (
            <UserDetails />
          ) : (
            <Navigate to="/dashboard" />
          )
        }
      />
      <Route
        path="/dashboard"
        element={userRole ? <Dashboard /> : <Navigate to="/" />}
      />
      <Route path="/" element={<Login />} />
      <Route path="*" element={<NotFound />} />
    </Routes>
  );
}

export default App;
