import React, { useState } from 'react';

function CreateBooking() {
  const [formData, setFormData] = useState({
    hotelname: '',
    bookingid: '',
    checkin: '',
    checkout: '',
    city: '',
    address: '',
    photos: [],
    desc: '',
    price: '',
    featured: false,
    status: 'waiting' // Assuming the default status is 'waiting'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`${API_BASE_URL}/booking`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });
      if (!response.ok) {
        throw new Error('Failed to create booking');
      }
      const data = await response.json();
      console.log('Booking created:', data);
      // Optionally, you can redirect the user to another page after successful booking creation
    } catch (error) {
      console.error('Error creating booking:', error.message);
    }
  };

  return (
    <div>
      <h2>Create Booking</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="hotelname">Hotel Name:</label>
        <input type="text" id="hotelname" name="hotelname" value={formData.hotelname} onChange={handleChange} required />

        <label htmlFor="bookingid">Booking ID:</label>
        <input type="text" id="bookingid" name="bookingid" value={formData.bookingid} onChange={handleChange} required />

        <label htmlFor="checkin">Check-in:</label>
        <input type="date" id="checkin" name="checkin" value={formData.checkin} onChange={handleChange} required />

        <label htmlFor="checkout">Check-out:</label>
        <input type="date" id="checkout" name="checkout" value={formData.checkout} onChange={handleChange} required />

        <label htmlFor="city">City:</label>
        <input type="text" id="city" name="city" value={formData.city} onChange={handleChange} required />

        <label htmlFor="address">Address:</label>
        <input type="text" id="address" name="address" value={formData.address} onChange={handleChange} required />

        {/* Assuming photos will be uploaded separately */}
        {/* <label htmlFor="photos">Photos:</label>
        <input type="file" id="photos" name="photos" multiple onChange={handlePhotoUpload} /> */}

        <label htmlFor="desc">Description:</label>
        <textarea id="desc" name="desc" value={formData.desc} onChange={handleChange} required />

        <label htmlFor="price">Price:</label>
        <input type="number" id="price" name="price" value={formData.price} onChange={handleChange} required />

        <label htmlFor="featured">Featured:</label>
        <input type="checkbox" id="featured" name="featured" checked={formData.featured} onChange={() => setFormData({ ...formData, featured: !formData.featured })} />

        <button type="submit">Create Booking</button>
      </form>
    </div>
  );
}

export default CreateBooking;
