import React, { useState, useEffect } from 'react';
import { fetchBookingsApproved } from '../../api';
import Navbar from './../navbar'; 
import './BookingList.css'

function BookingList() {
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const getBookings = async () => {
      try {
        const data = await fetchBookingsApproved();
        setBookings(data);
      } catch (error) {
        console.error('Error fetching bookings:', error.message);
      }
    };

    getBookings();
  }, []);

  const handleBuy = (bookingId) => {
    // Implement your buy logic here
    console.log(`Buy button clicked for booking ID: ${bookingId}`);
  };

  return (
    <div>
      <Navbar /> 
      <div className="booking-container">
        {bookings.map((booking) => (
          <div key={booking._id} className="booking-item">
            <div>
              <h3>{booking.hotelname}</h3>
              <p>Check-in: {booking.checkin}</p>
              <p>Check-out: {booking.checkout}</p>
              <p>Price: ${booking.price}</p>
              <button onClick={() => handleBuy(booking._id)}>Buy</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
export default BookingList;
