import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../../api';
import './Register.css'; // Import the CSS file

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [registered, setRegistered] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault(); // Prevent default form submission

    try {
      await register(username, email, password);
      setRegistered(true); // Set registered state to true
      setTimeout(() => {
        navigate('/login'); // Redirect to the login page after 2 seconds
      }, 2000);
    } catch (error) {
      console.error('Registration failed:', error.message);
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      {registered && <p className="registered-message">Registered</p>} {/* Display "Registered" message if registered is true */}
      <form className="register-form" onSubmit={handleRegister}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required // Make the field required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required // Make the field required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required // Make the field required
        />
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Register;
